package stack;
/**
 * A StackListItem for an object and a pointer to another StackListItem
 * 
 * http://www.f4.fhtw-berlin.de/~weberwu/info2/Handouts/Handout-LinkedList.doc
 * @author Tobias Preuss (s0516424) & Niels Richter (s0517512)
 */
public class StackListItem {
	public Object data;
	public StackListItem next;

	/**
	 * Constructor of StackListLink
	 * @param o
	 */
	public StackListItem(Object o) {
		data = o;
	}

	/**
	 * Output of the current data of the element in the list
	 * @return a String in form DATA:
	 */
	public String displayObject() {
		String s = data + ":";
		return s;
	}
}
