package stack;
/**
 * StackOverflow for the package stack
 * 
 * @author Tobias Preuss (s0516424) & Niels Richter (s0517512)
 */
public class StackOverflow extends Exception {
	private static final long serialVersionUID = 1L;

	/**
	 * If their is a StackOverFlow, print it to the console
	 * @param string
	 */
	public StackOverflow(String string) {
		System.out.println(string);
	}
}
